package mypackage;

import java.util.Scanner;

public class CompoundOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc =new Scanner(System.in);
		System.out.println("enter  number");
		 int a=sc.nextInt();
		a += 2;
		System.out.println("Result after +=2 is: "+a);
		a-= 9;
		System.out.println("Result after -=9 is: "+a);
		a *= 7;
		System.out.println("Result after *=7 is: "+a);
		a /= 8;
		System.out.println("Result after /=8 is: "+a);
		a %= 10;
		 System.out.println("Result after %=10 is: "+a);
		           
		           
		           
	
		
		
		

	}

}
