package mypackage;

public class AllDataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			int a=12;
			Integer i=new Integer(a);
			float f=25.67f;
			Float g=new Float(f);
			double d=567.890d;
			Double v=new Double(d);
			char ch='A';
			Character c=new Character(ch);
			short s=23;
			Short sc = new Short(s);
			boolean b=true;
			Boolean be=new Boolean(b);
			byte bu=4;
			Byte br=new Byte(bu);
			long l =233456l;
			Long lg=new Long(l);
			System.out.println("intger value  is: "+i);
			System.out.println("float value  is: "+g);
			System.out.println("double value  is: "+v);
			System.out.println("long value  is: "+lg);
			System.out.println("short value  is: "+sc);
			System.out.println("boolean value is: "+be);
			System.out.println("byte value is: "+br);
			
			
			

	}

}
